(48, 'Steffen Kiefer', 'steffen-kiefer', '1724647698507577168', 'International Co-Ordinator (Germany)', '2', '0', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '1', 3, '', 'whatsapp-image-2024-08-26-at-10-3Vc2sXbCfYSiJvhPL91u5kSifldmSvLBgae54ChF.jpeg', '0', '1', '0', '0', '0', '0', 'en', '2023-01-04 06:57:39', '2024-08-26 04:48:18'),
(49, 'Jakob Urth', 'jakob-urth', '1721105334164412530', 'International Leader (Scandinavia & Europa)', '2', '0', 'https://www.facebook.com/jakob.urth', 'https://www.instagram.com/urth_adventures/', NULL, NULL, '+47 41219806', 'jakob@urthadventures.com', '<p style=\"text-align: justify;\">Jakob (born in 1976) is a full-time adventurer, professional mountain climber, Guide, Author, Adventure Photographer, and Owner of Urth-Adventures. He has 25+ years of experience in the high mountains worldwide, including 9 expeditions to 8000-meter peaks and 70+ summits of 3000 to 8000-meter mountains.&nbsp;</p>\r\n<p style=\"text-align: justify;\">Together with <strong>Seven Summits Treks</strong><strong>,</strong> he offers his knowledge and experience as a western mountain guide. On all expeditions, he can get booked directly as a Private Guide/Group and High-altitude photographer. (Contact Jakob directly or via SST)</p>\r\n<p style=\"text-align: justify;\"><strong>NEXT:</strong></p>\r\n<p style=\"text-align: justify;\">He will be guiding expeditions to Aconcagua, Ama Dablam, and other big peaks with <strong>Seven Summit Treks.</strong></p>\r\n<p style=\"text-align: justify;\"><strong>High-Altitude Photographer</strong></p>\r\n<p style=\"text-align: justify;\">Jakob has been taking photos/films on Mount Everest (TV2 Denmark Everest documentary 2018 and 2023) Private photographer on Ama Dablam, Manaslu, Aconcagua, and so on. He is an author/writer of books and articles.</p>', '<p>Mountain Guide, Photographer &amp; Adventurer&nbsp;</p>', '1', 4, '', 'bf177959-173b-4d2c-81f8-5747adc51c2f-uKUdSVOZjpVistnsNyWj2Fn4ubFpwl1nKeR3AnBm.jpg', '1', '1', '0', '0', '0', '0', 'en', '2023-01-04 06:58:34', '2024-07-16 04:48:54'),
