(91, 'Manish Maharjan', 'manish-maharjan', '1723177108593955178', 'Filmmaker / Photographer', '4', '11', 'https://www.facebook.com/manishmaharjan444', 'https://www.instagram.com/manish.maharjan444/', NULL, NULL, NULL, NULL, NULL, NULL, '1', 25, 'manish-maharjan-7MyYd70KLIfSmIgo343hsxaBkauKvOKYRTd7KIB5.PNG', 'manish-maharjan-AMP13gJEw3TtAS7yhSp0oDaVQy3FBID0JNaJDi0z.PNG', '1', '1', '0', '0', '0', '0', 'en', '2024-08-05 05:55:11', '2024-08-09 04:18:28'),
(92, 'Shayuj Pokhrel', 'shayuj-pokhrel', '1723176790964422786', 'Filmmaker / Photographer', '4', '11', 'https://www.facebook.com/syuz.pokhrel.5', 'https://www.instagram.com/shayujpokhrel/', NULL, NULL, '+977 9801238848', 'info@sevensummittreks.com', '<p style=\"text-align: justify;\">Shayuj Pokhrel is a young and talented Creative filmmaker with a keen eye for visual storytelling. With over five years in the industry, he has honed his skills, crafting compelling narratives for a diverse clientele, from prestigious institutions like the U.S. Embassy to emerging musical artists, Miss World, Kristin Harila, and many other famous brands from Nepal.</p>\r\n<p style=\"text-align: justify;\">His expertise spans visual direction, editing, and cinematography, backed by a strong technical foundation in software like Premier Pro, After Effects, and Photoshop. His ability to blend creativity with technical proficiency is evident in his work. We believe Shayuj\'s talent and enthusiasm will be an invaluable asset to Seven Summit Treks, helping us create unforgettable visual experiences that capture the essence of our adventures.</p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n<p style=\"text-align: justify;\"><strong>Youtube channel:</strong></p>\r\n<p style=\"text-align: justify;\"><strong><a href=\"https://www.youtube.com/@shayujpokhrel8234\">https://www.youtube.com/@shayujpokhrel8234</a></strong></p>\r\n<p style=\"text-align: justify;\">&nbsp;</p>', NULL, '1', 26, '', 'new-1xkc4uQc0Q7ydNVnbuFTMznomVdjOArqRzkOB18I.png', '1', '1', '0', '0', '0', '0', 'en', '2024-08-05 05:57:57', '2024-08-09 04:13:10'),
